INSERT INTO preguntasc (pregunta, opciones, respuesta_correcta) VALUES ('¿Qué es necesario, además de una infraestructura a nivel de hardware para poner en marcha un sistema informático en una empresa?', ARRAY['Software específico para la gestión empresarial', 'Sistema de contabilidad', 'Programa para la gestión comercial', 'Elementos de facturación'], 'Software específico para la gestión empresarial');
INSERT INTO preguntasc (pregunta, opciones, respuesta_correcta) VALUES ('¿Qué se destaca como una ventaja a la hora de programar un sistema ERP?', ARRAY['Mayor facilidad de tareas de personalización', 'Menor esfuerzo requerido', 'Garantía de robustez y funcionalidad', 'Análisis profundo no necesario'], 'Mayor facilidad de tareas de personalización');
INSERT INTO preguntasc (pregunta, opciones, respuesta_correcta) VALUES ('¿Cuál es la opción más recomendable para la mayoría de organizaciones al implementar un sistema ERP?', ARRAY['Utilizar un sistema ERP existente y de buena calidad', 'Desarrollar un sistema ERP desde cero', 'Programar el sistema ERP en Java', 'Evitar opciones de personalización'], 'Utilizar un sistema ERP existente y de buena calidad');
INSERT INTO preguntasc (pregunta, opciones, respuesta_correcta) VALUES ('¿Qué ofrecen los sistemas ERP existentes?', ARRAY['Amplias opciones de personalización funcional y visual', 'Menos opciones de personalización', 'Garantía de análisis profundo', 'Complejidad en la toma de decisiones'], 'Amplias opciones de personalización funcional y visual');
INSERT INTO preguntasc (pregunta, opciones, respuesta_correcta) VALUES ('¿Qué se considera una licencia libre?', ARRAY['Una licencia libre la modificación y distribución del software', 'Una licencia libre implica que el software sea siempre gratuito', 'Una licencia libre implica que el software sea de pago', 'Una licencia libre se hace de pago a los tres meses de adquirirlo'], 'Una licencia libre la modificación y distribución del software');
